var self = module.exports;

self.generateSuccessResponse = function(data) {    
    return { 
        headers: {
            "Access-Control-Allow-Origin" : process.env.ALLOWED_ORIGINS,
            "Access-Control-Allow-Headers": "*"
        },
        statusCode: 200,
        body: JSON.stringify({status: "success", "data": data})
    }
} 

self.generateCustomResponse = function(status, data) {    
    return { 
        headers: {
            "Access-Control-Allow-Origin" : process.env.ALLOWED_ORIGINS,
            "Access-Control-Allow-Headers": "*"
        },
        statusCode: 200,
        body: JSON.stringify({"status": status, "data": data})
    }
} 

self.generateErrorResponse = function(errMessage) {
    return { 
        headers: {
            "Access-Control-Allow-Origin" : process.env.ALLOWED_ORIGINS,
            "Access-Control-Allow-Headers": "*"
        },
        statusCode: 200,
        body: JSON.stringify({status: "error", message: errMessage})
    }
}
